        '^' => Coordinate {
            x: current_coordinate.x,
            y: current_coordinate.y + 1,
        },
        '>' => Coordinate {
            x: current_coordinate.x + 1,
            y: current_coordinate.y,
        },
        'v' => Coordinate {
            x: current_coordinate.x,
            y: current_coordinate.y - 1,
        },
        '<' => Coordinate {
            x: current_coordinate.x - 1,
            y: current_coordinate.y,
